using System;
using System.Collections.Generic;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Threading.Tasks;


namespace titanZPL {
    public partial class titanZPL {
        public string zpl_data_base64="";

        public Bitmap           label;
        public Graphics         graphics;
        public Brush            brush;
        public Pen              pen;
        public List<String>     err                 =new List<string>();
        public string           position_mode       ="";
        public int              _dpi                =203;                   //152,203,300,608 dpi
        public Rectangle        label_bounds        =new Rectangle();       //offset+width/height of label
        public bool             use_titan_engine    =true;
        public bool             redraw              =true;
        public void titanZPL_variables() {
            brush=Brushes.Black;
            pen=Pens.Black;
            label_bounds.Width=800;
            label_bounds.Height=600;
        }//end init

        public titanZPL() {
            titanZPL_variables();
        }

        public titanZPL(string data) {
            titanZPL_variables();
            load_document(data);
            build();
            draw();
        }

        public void set_label_type(float w,float h,float dpi) {
            label_bounds.Width =(int)(w*dpi);
            label_bounds.Height=(int)(h*dpi);
            _dpi=(int)dpi;
        }

        public void test_zpl() {
            //build_raster_fonts_from_printer.generate_raster_fonts();
            load_document(zpl_data_base64);   
            build();
            draw();
        }

        public void load(string zpl) {
            load_document(zpl);   
            build();
            draw();
        }
    }//end class
}//end namespace
